mapper.select:
select
  id, imageurl, imagetitle, imagetype, imagestate
from image


        and id = #{id}


        and imageurl = #{imageurl}


        and imagetitle = #{imagetitle}


        and imagetype = #{imagetype}


        and imagestate = #{imagestate}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from image


        and id = #{id}


        and imageurl = #{imageurl}


        and imagetitle = #{imagetitle}


        and imagetype = #{imagetype}


        and imagestate = #{imagestate}
