mapper.select:
select
  id, proexplain, proprice, prostep, protypeid, proname, imageid, prostatus, busid, tecid
from project


        and id = #{id}


        and proexplain = #{proexplain}


        and proprice = #{proprice}


        and prostep = #{prostep}


        and protypeid = #{protypeid}


        and proname = #{proname}


        and imageid = #{imageid}


        and prostatus = #{prostatus}


        and busid = #{busid}


        and tecid = #{tecid}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from project


        and id = #{id}


        and proexplain = #{proexplain}


        and proprice = #{proprice}


        and prostep = #{prostep}


        and protypeid = #{protypeid}


        and proname = #{proname}


        and imageid = #{imageid}


        and prostatus = #{prostatus}


        and busid = #{busid}


        and tecid = #{tecid}

SELECT
p.id pid, p.proexplain proexplain, p.proprice proprice, p.prostep prostep, p.protypeid protypeid, p.proname proname, p.imageid imageid, p.prostatus prostatus, p.busid busid, p.tecid tecid,
i.imageurl imageurl from project p,image i where p.imageid = i.id and p.prostatus = '1' limit #{page},#{limit}
